mapper.select:
SELECT a.id, a.draw_code drawCode, a.draw_type drawType, a.code, a.user_type userType, a.realname, a.bankname, a.banknum, a.bankaddress, a.bank_branch bankBranch, a.money, a.cash_withdrawal_fee cashWithdrawalFee, a.total_money totalMoney, a.add_time addTime, a.status, a.remark, a.draw_time drawTime, a.verify_id verifyId, a.verify_name verifyName, a.verify_time verifyTime, a.verify_status verifyStatus, a.finance_id financeId, a.finance_name financeName, a.finance_time financeTime, a.finance_status financeStatus, a.draw_name drawName, (SELECT COUNT(*) FROM ecm_mzf_draw b WHERE b.code = a.code AND FROM_UNIXTIME(b.add_time, '%Y-%m-%d') = FROM_UNIXTIME(a.add_time, '%Y-%m-%d')) drawNo FROM ecm_mzf_draw a WHERE 1 = 1
and draw_code = #{drawCode,jdbcType=VARCHAR}
and draw_type = #{drawType,jdbcType=BIT}
and code = #{code,jdbcType=VARCHAR}
and realname = #{realname,jdbcType=VARCHAR}
and user_type = #{userType,jdbcType=VARCHAR}
and add_time <= #{addTimeStart,jdbcType=INTEGER}
and add_time >= #{addTimeEnd,jdbcType=INTEGER}
and status = #{status,jdbcType=BIT}
and draw_name = #{drawName,jdbcType=VARCHAR}
ORDER BY add_time DESC

	select count(*) from ecm_mzf_draw WHERE 1 = 1

     and draw_code = #{drawCode,jdbcType=VARCHAR}


     and draw_type = #{drawType,jdbcType=BIT}


      and code = #{code,jdbcType=VARCHAR}


      and realname = #{realname,jdbcType=VARCHAR}


      and user_type = #{userType,jdbcType=VARCHAR}	


      and add_time <= #{addTimeStart,jdbcType=INTEGER}


      and add_time >= #{addTimeEnd,jdbcType=INTEGER}


      and status = #{status,jdbcType=BIT}


      and draw_name = #{drawName,jdbcType=VARCHAR}

mapper.update:
update ecm_mzf_draw set status=#{status}, finance_status=#{financeStatus},
remark = #{remark},
finance_id=#{financeId}, finance_name=#{financeName}, finance_time=#{financeTime} where draw_code = #{drawCode}

mapper.select:
  	SELECT 
	  COUNT(*) 
	FROM
	  ecm_mzf_draw_water 
	WHERE code = #{code} 
	  AND DATE_FORMAT(draw_time, '%Y-%m-%d') = #{nowTime}

  	SELECT 
  COUNT(*) AS tp_count 
FROM
  ecm_mzf_draw a
WHERE (
    FROM_UNIXTIME(a.add_time, '%Y-%m-%d') = #{nowTime} 
    and (a.status != 3 or a.status != 5) 
    and a.code = #{code} 
    and a.draw_type = #{drawType}
  ) 
LIMIT 10
